package LearnOops;

public class LearnConditions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int age = 34;
		
		if (age < 18) {
			
			System.out.println("minor");
			
		}else if (age >=18 && age<60) {
			
			System.out.println("adult");
			
		}if (age>=60) {
			System.out.println("seniour citizen");
		}

	}

}
